const offset=100;const scrollUp=document.querySelector('.scroll-up');const scrollUpSvgPath=document.querySelector('.scroll-up__svg-path');const pathLength=scrollUpSvgPath.getTotalLength();const scar=document.querySelector('.scroll-up__svg-arrow-path');scrollUpSvgPath.style.strokeDasharray=`${pathLength} ${pathLength}`;scrollUpSvgPath.style.transition='stroke-dashoffset 20ms';
const getTop=()=>window.pageYOffset||document.documentElement.scrollTop;
const updateDashOffset=()=>{const height=document.documentElement.scrollHeight-window.innerHeight;const dashoffset=pathLength-(getTop()*pathLength/height);scrollUpSvgPath.style.strokeDashoffset=dashoffset;return dashoffset};
window.addEventListener('scroll',()=>{d=updateDashOffset();if(getTop()>offset){if(d==0){scar.style.fill='orange';}else{scar.style.fill='#dcdcdc';}scrollUp.classList.add('active');}else{scrollUp.classList.remove('active');}});
scrollUp.addEventListener('click',()=>{scar.style.fill='orange';window.scrollTo({top:0,behavior:'smooth'});});